local num = 3354   --对应上面的附魔个数2769   

function UseItemEvent(event, player, item, target)  
	local nun1 = player:GetItemCount(IiemID_0) 
	local nun2 = player:GetItemCount(IiemID_1)  
    local x1 = Rids[math.random(1,num)]
    local x2 = Rids[math.random(1,num)]	
	if(player:IsInCombat())then
	player:SendAreaTriggerMessage("你在战斗中！")
	player:SendBroadcastMessage("你在战斗中！")
else

if(nun1>0)then
        player:SendBroadcastMessage("洗炼锁定第1条属性")
   else    
        target:SetEnchantment(x1, 0)           
end		                                                                                        

if(nun2>0)then
        player:SendBroadcastMessage("洗炼锁定第2条属性")                                                              
    else
        target:SetEnchantment(x2, 1)	
end		                                                                                                                                                          
end		                                                                                          
	player:RemoveItem(IiemID_7, 1)    --删除附魔物品
	player:RemoveItem(IiemID_0, 1)    --删除锁定物品1
	player:RemoveItem(IiemID_1, 1)    --删除锁定物品2
end
RegisterItemEvent(IiemID_7,2,UseItemEvent)
